// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

contract CrowdFundingWithDeadline {
    enum Status {
        Ongoing,
        Failed,
        Succeeded,
        PaidOut
    } // internally recorded as 0,1,2,3

    string public name;
    uint256 public targetAmount;
    uint256 public fundingDeadline;
    address public beneficiary;
    Status public status;
    mapping(address => uint256) amounts;
    uint256 public totalCollected;
    bool public collected;

    modifier inState(Status expectedStatus) {
        require(status == expectedStatus, "Invalid State");
        _;
    }

    function contribute() public payable inState(Status.Ongoing) {
        amounts[msg.sender] += msg.value;
        totalCollected += msg.value;

        if (totalCollected >= targetAmount) {
            collected = true;
        }
    }

    function isClosed() public view returns (bool) {
        return block.timestamp > fundingDeadline || status != Status.Ongoing;
    }

    function tryClosing() public returns (bool) {
        require(totalCollected >= targetAmount, "Amount not met");
        require(block.timestamp > fundingDeadline, "Time left");
        status = Status.Succeeded;
        return true;
    }

    constructor(
        string memory _contractName,
        uint256 _targetAmountEth,
        uint256 _durationInMin,
        address payable _beneficiary
    ) {
        name = _contractName;
        targetAmount = _targetAmountEth * 1 ether; // convert ether to wei, and save as wei
        fundingDeadline = block.timestamp + (_durationInMin * 1 minutes);
        beneficiary = _beneficiary;
        status = Status.Ongoing;
    }
}

contracts/Crud.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

contract Crud {
    struct User {
        uint256 id;
        string name;
    }
    User[] public users;

    uint256 public nextId = 1;

    function createUser(string memory name) public {
        users.push(User(nextId, name));
        nextId++;
    }

    // this return signature will allow us to use DeStructuring at the consumer side
    function findUserById(uint256 id) public view returns (uint256, string memory) {
        uint256 i = findUserIndex(id);
        return (users[i].id, users[i].name);
    }

    function updateUser(uint256 id, string memory name) public {
        uint256 i = findUserIndex(id);
        users[i].name = name;
    }

    function deleteUserById(uint256 id) public {
        uint256 i = findUserIndex(id);
        delete users[i];
    }

    function findUserIndex(uint256 id) internal view returns (uint256) {
        for (uint256 i = 0; i < users.length; i++) {
            if (users[i].id == id) return i;
        }
        revert("User does not exist");
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Import this file to use console.log
import "hardhat/console.sol";

contract Lock {
    uint256 public unlockTime;
    address payable public owner;

    event Withdrawal(uint256 amount, uint256 when);

    constructor(uint256 _unlockTime) payable {
        require(block.timestamp < _unlockTime, "Unlock time should be in the future");

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

